const crypto = require('crypto');

const incomingSignature = $input.first().json.headers['x-n8n-signature'];
const body = JSON.stringify($input.first().json.body);
const secret = $env.N8N_WEBHOOK_SECRET || '';

const expectedSignature = crypto.createHmac('sha256', secret)
  .update(body)
  .digest('hex');

if (incomingSignature !== expectedSignature) {
  throw new Error('Invalid HMAC signature');
}

return [{
  json: {
    verified: true,
    payload: $input.first().json.body
  }
}];

const payload = $json.payload;

return [{
  json: {
    event: payload.event,
    instance_id: payload.instance_id,
    template_task: payload.template_task,
    new_assignee_profile_id: payload.new_assignee_profile_id,
    new_assignee_email: payload.new_assignee_email,
    old_assignee_profile_id: payload.old_assignee_profile_id,
    location_id: payload.location_id,
    timestamp: payload.timestamp,
    status: 'processed'
  }
}];
