// Date formatting helpers (match Google Forms format)
function fmtTimestamp(iso) {
  const d = new Date(iso);
  if (isNaN(d.getTime())) return iso;
  const M = d.getMonth() + 1;
  const D = d.getDate();
  const Y = d.getFullYear();
  const h = d.getHours();
  const m = String(d.getMinutes()).padStart(2, '0');
  const s = String(d.getSeconds()).padStart(2, '0');
  return `${M}/${D}/${Y} ${h}:${m}:${s}`;
}
function fmtDate(val) {
  const d = new Date(val);
  if (isNaN(d.getTime())) return String(val);
  return `${d.getMonth() + 1}/${d.getDate()}/${d.getFullYear()}`;
}
function fmtTime(val) {
  const d = new Date(val);
  if (isNaN(d.getTime())) return String(val);
  let h = d.getHours();
  const ap = h >= 12 ? 'PM' : 'AM';
  h = h % 12 || 12;
  const m = String(d.getMinutes()).padStart(2, '0');
  const s = String(d.getSeconds()).padStart(2, '0');
  return `${h}:${m}:${s} ${ap}`;
}

// Get sheet column headers (skip n8n metadata 'row_number')
const sheetRow = $('Read Sheet Headers').first().json;
const headers = Object.keys(sheetRow).filter(h => h !== 'row_number');

const payload = $('Webhook').first().json.body;

// SPARSE mapping: only include columns that match field responses
// This prevents overwriting other columns when updating an existing row
// (e.g., weekly form updating weekly columns without clearing daily columns)
const row = {};
let lookupColumn = null;
let lookupValue = null;

// Set Timestamp in first column
if (headers.length > 0) {
  row[headers[0]] = fmtTimestamp(payload.timestamp);
}

// Name-based matching: match each field to its sheet column
// Uses sheet_header (exact Google Sheet column name) if available, falls back to label
for (const f of payload.field_responses) {
  const matchName = f.sheet_header || f.label;
  const matched = headers.find(h =>
    h === matchName || h.trim() === matchName || h.trim() === matchName.trim()
  );
  if (matched) {
    let value;
    if (f.value === null || f.value === undefined) {
      value = '';
    } else if (f.field_type === 'date') {
      value = fmtDate(f.value);
    } else if (f.field_type === 'datetime') {
      value = fmtTime(f.value);
    } else {
      value = String(f.value);
    }
    row[matched] = value;

    // Use the first date field as the lookup column for upsert
    // This allows multiple forms sharing the same sheet to match rows by date
    if (f.field_type === 'date' && !lookupColumn) {
      lookupColumn = matched;
      lookupValue = value;
    }
  }
}

// Add signature/selfie URLs if present
if (payload.completion_signature) {
  row['Signature'] = payload.completion_signature;
}
if (payload.completion_selfie) {
  row['Selfie'] = payload.completion_selfie;
}

// Store lookup info as metadata for the Append Or Update node
row['__lookupColumn'] = lookupColumn || headers[0];
row['__lookupValue'] = lookupValue || row[headers[0]];

return [{ json: { ...row } }];
