const crypto = require('crypto');

const incomingSignature = $input.first().json.headers['x-n8n-signature'];
const body = JSON.stringify($input.first().json.body);
const secret = $env.N8N_WEBHOOK_SECRET || '';

const expectedSignature = crypto.createHmac('sha256', secret)
  .update(body)
  .digest('hex');

if (incomingSignature !== expectedSignature) {
  throw new Error('Invalid HMAC signature');
}

return [{
  json: {
    verified: true,
    payload: $input.first().json.body
  }
}];

const payload = $json.payload;

// Skip if no Google Sheet configured
if (!payload.google_sheet_id) {
  return [{ json: { skip: true, reason: 'No Google Sheet ID configured' } }];
}

// Build header row from field labels
const headers = [
  'Timestamp',
  'Submitted By',
  'Status',
  'Overall Pass',
  ...payload.field_responses.map(f => f.label)
];

// Build data row from field values
const values = [
  payload.timestamp,
  payload.submitted_by_name || payload.submitted_by_profile_id,
  payload.status,
  payload.overall_pass === null ? '' : payload.overall_pass ? 'PASS' : 'FAIL',
  ...payload.field_responses.map(f => {
    if (f.value === null || f.value === undefined) return '';
    if (typeof f.value === 'boolean') return f.value ? 'Yes' : 'No';
    return String(f.value);
  })
];

return [{
  json: {
    skip: false,
    google_sheet_id: payload.google_sheet_id,
    google_sheet_tab: payload.google_sheet_tab || 'Sheet1',
    headers: headers,
    values: values,
    response_id: payload.response_id,
    form_template_name: payload.form_template_name,
    binder_name: payload.binder_name
  }
}];

// Convert headers + values into a key-value object for Google Sheets append
const headers = $('Prepare Sheet Data').first().json.headers;
const values = $('Prepare Sheet Data').first().json.values;

const row = {};
for (let i = 0; i < headers.length; i++) {
  row[headers[i]] = values[i] || '';
}

return [{ json: row }];
